import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Product() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch('https://fakestoreapi.com/products/categories')
      .then((response) => response.json())
      .then((data) => setCategories(data))
      .catch((error) => console.error('Error fetching categories:', error));
  }, []);

  const getImagePath = (category) => {
    // Create a sanitized version of the category name for the image file
    const sanitizedCategory = category
      .toLowerCase()
      .replace(/ /g, '-') // replace spaces with hyphens
      .replace(/[^a-z0-9-]/g, ''); // remove any non-alphanumeric or hyphen characters

    // Construct the image path
    const imagePath = `/images/${sanitizedCategory}.jpg`;
    console.log(imagePath);
    return imagePath;
  };

  return (
    <div className="card-product-container">
      {categories.length > 0 ? (
        categories.map((category) => {
          const imagePath = getImagePath(category);
          return (
            <div key={category} className="products-card">
              <Link to={`/${category.toLowerCase().replace(/ /g, '')}`}>
                <img
                  src={imagePath}
                  alt={category}
                  onError={(e) => {
                    e.target.src = '/images/default.jpg'; // Fallback image
                  }}
                />
              </Link>
              <h2>{category}</h2>
              <p>Explore the latest in {category.toLowerCase()} products.</p>
            </div>
          );
        })
      ) : (
        <p>Loading categories...</p>
      )}
    </div>
  );
}

export default Product;
